listitem.programlisting:
// Connection reader
case class CRead[A](rd: Connection => A)

val getPassword: CRead[String] = CRead(_.query("SELECT pwd FROM user"))
def validatePassword(p: String): CRead[Boolean] = getPassword map (_ == p)
def setPassword(p: String): CRead[Unit] = CRead(_.execute("UPDATE user " + p))

// Ignore the Connection context, lift into Read
def point[A](a: => A): CRead[A] =
    CRead(_ => a)
