sect2.para:
      State is a monad too!

case class State[Cx, A](run: Cx => (A, Cx)) {
  def map[B](f: A => B): State[Cx, B] =
      State(x => {
        val (a, t) = run(x)
        (f(a), t)
      })
  def flatMap[B](f: A => State[Cx, B]): State[Cx, B] =
      State(x => {
        val (a, t) = run(x)
        f(a) run t
      })
}
